import { useEffect, useState, useMemo, useCallback } from "react";

const Parent = () => {
    const [data, setData] = useState([]);
    const [error, setError] = useState('');
    const [search, setSearch] = useState('');
    const [selectedUser, setSelectedUser] = useState(null);  // State to store the selected user's details

    useEffect(() => {
        fetch("./data.json")
            .then(response => response.json())
            .then(json => setData(json))
            .catch(error => setError("Failed to load data"));
    }, []);

    const filteredProducts = useMemo(() => {
        return data.filter(item =>
            item.gender.toLowerCase().includes(search.toLowerCase())
        );
    }, [data, search]);

    if (error) {
        return <p>{error}</p>;
    }

    // useCallback to memoize handleClick function
    const handleClick = useCallback((user) => {
        setSelectedUser(user); // Store the clicked user in the selectedUser state
    }, []); // Empty dependency array means the function is created once and memoized

    return (
        <div className="cards">
            <div className="in">
                <input
                    type="text"
                    placeholder="What product would you like to buy?"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
            </div>

            <h1>Your search history: <span>{search}</span></h1>
            <h3>Browse by category</h3>

            <div className="product-card">
                {filteredProducts.length > 0 ? (
                    filteredProducts.map((item, index) => (
                        <div key={index}>
                            <div className="card">
                                <button onClick={() => handleClick(item)}>{item.firstName}</button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No Movies found</p>
                )}
            </div>

            {/* Conditionally render the selected user's details */}
            {selectedUser && (
                <div className="user-details">
                    <h2>User Details</h2>
                    <p><strong>Name:</strong> {selectedUser.firstName} {selectedUser.lastName}</p>
                    <p><strong>Gender:</strong> {selectedUser.gender}</p>
                    <p><strong>Age:</strong> {selectedUser.age}</p>
                </div>
            )}
        </div>
    );
};

export default Parent;
