import { useEffect, useState, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import { Route, Routes } from "react-router-dom";
import Child from "./Child"; // Import the Child component

const Parent = () => {
    const [data, setData] = useState([]);
    const [error, setError] = useState('');
    const [search, setSearch] = useState('');
    const [selectedUser, setSelectedUser] = useState(null); // State to store selected user
    const navigate = useNavigate();

    useEffect(() => {
        fetch("./data.json")
            .then(response => response.json())
            .then(json => setData(json))
            .catch(error => setError("Failed to load data"));
    }, []);

    const filteredProducts = useMemo(() => {
        return data.filter(item =>
            item.gender.toLowerCase().includes(search.toLowerCase())
        );
    }, [data, search]);

    if (error) {
        return <p>{error}</p>;
    }

    const handleClick = (user) => {
        setSelectedUser(user); // Store the selected user in state
        navigate('/Child');  // Navigate to the Child component
    };

    return (
        <div className="cards">
            <div className="in">
                <input
                    type="text"
                    placeholder="What product would you like to buy?"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
            </div>

            <h1>Your search history: <span>{search}</span></h1>
            <h3>Browse by category</h3>

            <div className="product-card">
                {filteredProducts.length > 0 ? (
                    filteredProducts.map((item, index) => (
                        <div key={index}>
                            <div className="card">
                                <button onClick={() => handleClick(item)}>{item.firstName}</button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No Movies found</p>
                )}
            </div>

            {/* Add Routes to handle navigation to Child */}
            <Routes>
                <Route path="/Child" element={<Child user={selectedUser} />} />
            </Routes>
        </div>
    );
};

export default Parent;
