import { useContext } from "react";
import Context from "../Context/Context";
const Child=()=>{
    const {theme,setTheme}=useContext()
    const handleToggle=()=>{
        setTheme((prevTheme)=>(prevTheme==='dark' ? 'light': 'dark'));
    }
    return(
        <div>
           <button onClick={handleToggle}>Toogle</button>
        </div>
    )
}
export default Child;
