import React, { useState } from "react";
import "./index.css";

const Home = () => {
  const [groups, setGroups] = useState([]); // State for groups
  const [showPopup, setShowPopup] = useState(false); // State for popup visibility
  const [groupName, setGroupName] = useState(""); // State for group name input
  const [groupColor, setGroupColor] = useState("#007bff"); // State for group color

  // Show popup for adding group
  const handleAddGroup = () => {
    setShowPopup(true);
  };

  // Handle creating a new group
  const handleCreateGroup = () => {
    if (groupName) {
      setGroups([...groups, { name: groupName, color: groupColor }]); // Add new group with name and color
      setGroupName("");
      setGroupColor("#007bff");
      setShowPopup(false);
    } else {
      alert("Please enter a group name."); // Alert if group name is empty
    }
  };

  return (
    <div className="card">
      <div className="center">
        <div className="sidebar">
          <h1>Pocket Notes</h1>
          <ul>
            {groups.map((group, index) => (
              <li
                key={index}
                style={{ backgroundColor: group.color }}
                className="group-item"
              >
                {group.name}
              </li>
            ))}
          </ul>
          <button className="add-btn" onClick={handleAddGroup}>
            +
          </button>
        </div>

        <div className="main-content">
          <img
            src="/assets/image-removebg-preview 1 (2).png"
            alt="homeimage"
            width={"400px"}
          />
          <h1>Pocket Notes</h1>
          <p>
            Send and receive messages without keeping your phone online. Use
            Pocket Notes on up to 4 linked devices and 1 mobile phone.
          </p>
        </div>
      </div>

      {/* Popup */}
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Create a Group</h3>
            <input
              type="text"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
              placeholder="Enter group name"
            />
            <label>Select Color:</label>
            <input
              type="color"
              value={groupColor}
              onChange={(e) => setGroupColor(e.target.value)}
            />
            <div className="popup-actions">
              <button onClick={handleCreateGroup}>Create</button>
              <button onClick={() => setShowPopup(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
