import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "./index.css";

const GroupDetails = () => {
  const { groupName } = useParams();

  // Example groups
  const groups = [
    { name: "My Notes", color: "#007bff" },
    { name: "My personal grp", color: "#9b59b6" },
    { name: "Javascript grp", color: "#f39c12" },
    { name: "HTML grp", color: "#1abc9c" },
    { name: "CSS Notes", color: "#d35400" },
    { name: "SQL Notes", color: "#3498db" },
    { name: "Python Notes", color: "#8e44ad" },
  ];

  const [newMessage, setNewMessage] = useState(""); // State for new input message

  // Handle Send button click to append the new message to the paragraph
  const handleSend = () => {
    const messageParagraph = document.getElementById("message-paragraph");
    if (newMessage.trim() !== "") {
      messageParagraph.textContent += " " + newMessage; // Append the new message
      setNewMessage(""); // Clear the input field after sending
    }
  };

  return (
    <div className="group-details">
      {/* Sidebar with group items */}
      <div className="sidebar">
        <ul>
          {groups.map((group, index) => (
            <li
              key={index}
              className="group-item"
              style={{ backgroundColor: group.color }}
            >
              {group.name}
            </li>
          ))}
        </ul>
      </div>

      {/* Main content */}
      <div className="main-content">
        {/* Group header */}
        <div className="header">
          <h2>{groupName}</h2>
        </div>

        {/* Paragraph content */}
        <div className="messages">
          <p id="message-paragraph">
            Another productive way to use this tool is to begin a daily writing
            routine. One way is to generate a random paragraph with the intention
            to rewrite it while still keeping the original meaning. The purpose
            here is to just get the writing started so that when the writer goes
            onto their day's writing projects, words are already flowing from
            their fingers.
          </p>
          <p className="timestamp">9 Mar 2023 • 10:10 AM</p>
        </div>

        {/* Input box */}
        <div className="input-box">
          <input
            type="text"
            placeholder="Enter your message"
            value={newMessage} // Bind input value to state
            onChange={(e) => setNewMessage(e.target.value)} // Update the input value in state
          />
          <button onClick={handleSend}>Send</button> {/* Send the new message */}
        </div>
      </div>
    </div>
  );
};

export default GroupDetails;
