import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Use useNavigate instead of useHistory

const GroupPage = () => {
  const { groupId } = useParams(); // Get the group id from the URL
  const navigate = useNavigate();  // Replace useHistory with useNavigate
  const [group, setGroup] = useState(null);
  const [paragraphs, setParagraphs] = useState([]);
  const [newParagraph, setNewParagraph] = useState('');

  useEffect(() => {
    // Fetch group details and paragraphs (you could replace with an API call)
    const groupDetails = {
      id: groupId,
      name: `Group ${groupId}`,
    };

    setGroup(groupDetails);
  }, [groupId]);

  const handleAddParagraph = () => {
    if (newParagraph) {
      setParagraphs([...paragraphs, newParagraph]);
      setNewParagraph('');
    } else {
      alert('Please enter some text.');
    }
  };

  const handleBack = () => {
    navigate('/'); // Use navigate() to go back to home page
  };

  return (
    <div className="group-page">
      <h2>{group ? group.name : 'Loading...'}</h2>

      <div className="paragraph-list">
        {paragraphs.length > 0 ? (
          paragraphs.map((para, index) => <p key={index}>{para}</p>)
        ) : (
          <p>No paragraphs yet.</p>
        )}
      </div>

      <div className="input-section">
        <textarea
          value={newParagraph}
          onChange={(e) => setNewParagraph(e.target.value)}
          placeholder="Type a paragraph..."
        ></textarea>
        <button onClick={handleAddParagraph}>Add Paragraph</button>
      </div>

      <button onClick={handleBack}>Back to Home</button>
    </div>
  );
};

export default GroupPage;
