import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./index.css";

const Home = () => {
  const navigate = useNavigate();

  const [showPopup, setShowPopup] = useState(false);
  const [groupName, setGroupName] = useState("");
  const [groupColor, setGroupColor] = useState("#007bff");
  const [groups, setGroups] = useState([
    { name: "My Notes", color: "#007bff" },
    { name: "My personal grp", color: "#9b59b6" },
    { name: "Javascript grp", color: "#f39c12" },
    { name: "HTML grp", color: "#1abc9c" },
    { name: "CSS Notes", color: "#d35400" },
    { name: "SQL Notes", color: "#3498db" },
    { name: "Python Notes", color: "#8e44ad" },
  ]);

  const handleGroupClick = (name) => {
    navigate(`/group/${name}`);
  };

  const handleCreateGroup = () => {
    if (groupName) {
      setGroups([...groups, { name: groupName, color: groupColor }]);
      setGroupName("");
      setGroupColor("#007bff");
      setShowPopup(false);
    } else {
      alert("Please enter a group name.");
    }
  };

  return (
    <div className="container">
      {/* Sidebar */}
      <div className="sidebar">
        <ul>
          {groups.map((group, index) => (
            <li
              key={index}
              className="group-item"
              style={{ backgroundColor: group.color }}
              onClick={() => handleGroupClick(group.name)}
            >
              {group.name}
            </li>
          ))}
        </ul>
        <button className="add-btn" onClick={() => setShowPopup(true)}>
          +
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h1>Pocket Notes</h1>
        <img src="assets/image-removebg-preview 1 (2).png" alt="name"/>
        <p>
          Send and receive messages without keeping your phone online. Use
          Pocket Notes on up to 4 linked devices and 1 mobile phone.
        </p>
        <p className="encryption-note">ðŸ”’ End-to-end encrypted</p>
      </div>

      {/* Popup */}
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Create a Group</h3>
            <input
              type="text"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
              placeholder="Enter group name"
            />
            <div className="color-options">
              <button
                onClick={() => setGroupColor("#007bff")}
                style={{ backgroundColor: "#007bff" }}
              ></button>
              <button
                onClick={() => setGroupColor("#9b59b6")}
                style={{ backgroundColor: "#9b59b6" }}
              ></button>
              <button
                onClick={() => setGroupColor("#f39c12")}
                style={{ backgroundColor: "#f39c12" }}
              ></button>
              <button
                onClick={() => setGroupColor("#1abc9c")}
                style={{ backgroundColor: "#1abc9c" }}
              ></button>
              <button
                onClick={() => setGroupColor("#d35400")}
                style={{ backgroundColor: "#d35400" }}
              ></button>
            </div>
            <div className="popup-actions">
              <button onClick={handleCreateGroup}>Create</button>
              <button onClick={() => setShowPopup(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
