import'./index.css';
import React, { useState } from "react";


 
function Home() {

  const [groups, setGroups] = useState([

    { id: 1, name: "My Notes", color: "#007bff" },

  ]);

  const [showPopup, setShowPopup] = useState(false);

  const [groupName, setGroupName] = useState("");

  const [selectedColor, setSelectedColor] = useState(null);
 
  const colors = ["#ff6666", "#ffcc66", "#66ff66", "#66ccff", "#9966ff"];
 
  const handleAddGroup = () => {

    setShowPopup(true);

  };
 
  const handleCreateGroup = () => {

    if (groupName && selectedColor) {

      const newGroup = {

        id: groups.length + 1,

        name: groupName,

        color: selectedColor,

      };

      setGroups([...groups, newGroup]);

      setGroupName("");

      setSelectedColor(null);

      setShowPopup(false);

    } else {

      alert("Please enter a group name and select a color.");

    }

  };
 
  return (
<div className="app">
<div className="sidebar">
<ul className="group-list">

          {groups.map((group) => (
<li key={group.id}>
<span style={{ backgroundColor: group.color }}>

                {group.name

                  .split(" ")

                  .map((word) => word[0])

                  .join("")

                  .toUpperCase()}
</span>

              {group.name}
</li>

          ))}
</ul>
<button className="add-group-btn" onClick={handleAddGroup}>

          +
</button>
</div>
 
      {showPopup && (
<div className="popup">
<h3>Create New Group</h3>
<input

            type="text"

            placeholder="Enter group name"

            value={groupName}

            onChange={(e) => setGroupName(e.target.value)}

          />
<div className="color-options">

            {colors.map((color, index) => (
<div

                key={index}

                style={{ backgroundColor: color }}

                className={selectedColor === color ? "selected" : ""}

                onClick={() => setSelectedColor(color)}
></div>

            ))}
</div>
<button onClick={handleCreateGroup}>Create</button>
</div>

      )}
</div>

  );

}
 
export default Home;
