import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "./index.css";

const GroupDetails = () => {
  const { groupName } = useParams();

 
  const groups = [
    { name: "My Notes", color: "#007bff" },
    { name: "My personal grp", color: "#9b59b6" },
    { name: "Javascript grp", color: "#f39c12" },
    { name: "HTML grp", color: "#1abc9c" },
    { name: "CSS Notes", color: "#d35400" },
    { name: "SQL Notes", color: "#3498db" },
    { name: "Python Notes", color: "#8e44ad" },
  ];

  const [newMessage, setNewMessage] = useState(""); 
  const [messages, setMessages] = useState([ 
    "Another productive way to use this tool is to begin a daily writing routine. One way is to generate a random paragraph with the intention to rewrite it while still keeping the original meaning. The purpose here is to just get the writing started so that when the writer goes onto their day's writing projects, words are already flowing from their fingers."
  ]);

  
  const handleSend = () => {
    if (newMessage.trim() !== "") {
      setMessages([...messages, newMessage]); 
      setNewMessage(""); 
    }
  };

  return (
    <div className="group-details">
     
      <div className="sidebar">
        <ul>
          {groups.map((group, index) => (
            <li
              key={index}
              className="group-item"
              style={{ backgroundColor: group.color }}
            >
              {group.name}
            </li>
          ))}
        </ul>
      </div>

     
      <div className="main-content">
       
        <div className="header">
          <h2>{groupName}</h2>
        </div>

        
        <div className="messages">
          {messages.map((message, index) => (
            <p key={index}>{message}</p>
          ))}
        </div>

        
        <div className="input-box">
          <input
            type="text"
            placeholder="Enter your message"
            value={newMessage} 
            onChange={(e) => setNewMessage(e.target.value)} 
          />
          <button onClick={handleSend}>Send</button> 
        </div>
      </div>
    </div>
  );
};

export default GroupDetails;
