import { useEffect, useState } from "react";
import "./index.css";

const UserDashboard = () => {
  const [data, setData] = useState([]); // State for card data
  const [error, setError] = useState(""); // State for handling errors
  const [inputText, setInputText] = useState(""); // State for new card input

  // Load initial data
  useEffect(() => {
    fetch("./data.json")
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch(() => setError("Failed to load data"));
  }, []);

  // Add functionality
  const handleAdd = () => {
    if (inputText.trim() !== "") {
      const newCard = {
        name: inputText,
        title: "Default Title",
        description: "Default Description",
        status: "Active",
      };
      setData([...data, newCard]); // Add the new card to the state
      setInputText(""); // Clear the input field
    }
  };

  // Alternative Delete Functionality
  const handleDelete = (index) => {
    const updatedData = data.slice(); // Create a shallow copy of the data array
    updatedData.splice(index, 1); // Remove the item at the specified index
    setData(updatedData); // Update the state
  };

  // Alternative Edit Functionality
  const handleEdit = (index) => {
    const updatedData = [...data]; // Clone the current data
    const newTitle = prompt("Edit title:", updatedData[index].title); // Prompt for new title
    if (newTitle && newTitle.trim()) {
      updatedData[index].title = newTitle; // Update the title of the specific item
      setData(updatedData); // Update the state
    }
  };

  // Render error if any
  if (error) {
    return <h1>{error}</h1>;
  }

  return (
    <div>
      {/* Add new card section */}
      <div>
        <input
          type="text"
          placeholder="Enter name"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button onClick={handleAdd}>Add</button>
      </div>

      {/* Render cards */}
      <div className="cards">
        {data.map((item, index) => (
          <div className="card" key={index}>
            <h1>{item.name}</h1>
            <h1>{item.title}</h1>
            <h1>{item.description}</h1>
            <h1>{item.status}</h1>
            <button onClick={() => handleEdit(index)}>Edit</button>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserDashboard;
