import { useEffect, useState, useMemo } from "react";
import "./index.css";
import SearchUser from "./SearchUser";
import dashBoard from "../UseContext/UseContext";
const UserDashboard = () => {
  const [data, setData] = useState([]);
  const [error, setError] = useState("");
  const [inputText, setInputText] = useState("");
  const [search, setSearch] = useState("");
  //const searchRef=useRef(null);

  useEffect(() => {
    fetch("./data.json")
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch(() => setError("Failed to load data"));
  }, []);

  const handleAdd = () => {
    if (inputText.trim() === "") {
      alert("Please enter a name");
      return;
    }

    const newCard = {
      name: inputText,
      title: "Default Title",
      description: "Default Description",
      status: "Pending",
    };

    setData([...data, newCard]);
    setInputText(""); 
  };

  const handleDelete = (index) => {
    const updatedData = data.slice();
    updatedData.splice(index, 1);
    setData(updatedData);
  };

  const handleEdit = (index) => {
    const updatedData = [...data];

    const newTitle = prompt("Edit title:", updatedData[index].title);
    const newDescription = prompt("Edit description:", updatedData[index].description);
    const newStatus = prompt("Edit status (Pending/In-Progress/Completed):", updatedData[index].status);

    if (newTitle && newTitle.trim()) updatedData[index].title = newTitle;
    if (newDescription && newDescription.trim()) updatedData[index].description = newDescription;
    if (newStatus && newStatus.trim()) updatedData[index].status = newStatus;

    setData(updatedData);
  };

  const filteredProducts = useMemo(() => {
    return data.filter((item) =>
      item.status.toLowerCase().includes(search.toLowerCase())
    );
  }, [data, search]);

  if (error) {
    return <h1>{error}</h1>;
  }

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Enter name"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button onClick={handleAdd}>Add</button>
      </div>

     

      <div className="cards">
        {filteredProducts.map((item, index) => (
          <div className="card" key={index}>
            <h1>{item.name}</h1>
            <h2>{item.title}</h2>
            <p>{item.description}</p>
            <p>{item.status}</p>
            <button onClick={() => handleEdit(index)}>Edit</button>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </div>
        ))}
      </div>
      <dashBoard.Provider value={{search,setSearch}}>
         <SearchUser/>
      </dashBoard.Provider>
    </div>
  );
};

export default UserDashboard;
