import { useEffect, useState } from "react";
import "./index.css";

const UserDashboard = () => {
  const [data, setData] = useState([]);
  const [error, setError] = useState("");
  const [inputText, setInputText] = useState(""); // State to track input value

  useEffect(() => {
    fetch("./data.json")
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch(() => setError("Failed to load data"));
  }, []);

 /* const handleAdd = () => {
    if (inputText.trim() !== "") {
      const newCard = {
        name: inputText,
        title: "Default Title", // Customize as needed
        description: "Default Description", // Customize as needed
        status: "Active", // Customize as needed
      };
      setData([...data, newCard]); // Add the new card to the list
      setInputText(""); // Clear the input
    }
  };

  const handleDelete = (index) => {
    const updatedData = data.filter((_, i) => i !== index);
    setData(updatedData); // Update the state with the filtered data
  };

  const handleEdit = (index) => {
    const newTitle = prompt("Enter new title:", data[index].title);
    if (newTitle !== null && newTitle.trim() !== "") {
      const updatedData = data.map((item, i) =>
        i === index ? { ...item, title: newTitle } : item
      );
      setData(updatedData); // Update the state with the edited data
    }
  };*/


  const handleTodoChange = (event) => {
    setInput(event.target.value);
}


const handleTodoSubmit = () => {
    if (input.trim() !== "") {
        setInputText([...inputText, input]);
        setInput("");  
    }
}


const handleDelete = (i) => {
    const updatedTasks = [...inputText]; 
    updatedTasks.splice(i, 1);  
    setInputText(updatedTasks); 
};


const handleEdit = (i) => {
    const newText = prompt("Edit your recipe:", inputText[i]);  
    if (newText && newText.trim() !== "") {  
        const updatedTasks = [...inputText];  
        updatedTasks[i] = newText;  
        setInputText(updatedTasks);  
    }
};

  if (error) {
    return <h1>{error}</h1>;
  }

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Enter name"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <button onClick={handleAdd}>Add</button>
      </div>
      <div className="cards">
        {data.map((item, index) => (
          <div className="card" key={index}>
            <h1>{item.name}</h1>
            <h1>{item.title}</h1>
            <h1>{item.description}</h1>
            <h1>{item.status}</h1>
            <button onClick={() => handleEdit(index)}>Edit</button>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserDashboard;
